import React, { useState, useHistory } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import './App.css';
import Google from './components/googleLogin'
import InputCollaborateur from './components/inputCollaborateur';
import Rh from './components/rh'
import axios from './axios'



function App() {

  const [collabo, setCollabo] = useState({})
  let history = useHistory();

  const collboratorResponse = () => {
    axios.post("http://localhost:5000/gestionPerso", { email: localStorage.getItem("name") })
      .then(response => {
        setCollabo(response.data.isCollabo)
      })
      .then(collabo ? history.push(`/rh`) : history.push(`/collaborateur/${nomPrenom}`))
  }

  return (
    <Router>
      <div>
        <Switch>
          <Route exact path='/'><Google />
          }</Route>
          {/* <Route exact path='/redirect/:email'><Redirection /></Route> */}
          <Route exact path='/collaborateur/:prenom/:nom'><InputCollaborateur /></Route>
          <Route exact path='/rh'><Rh /></Route>}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
