import React, { useState, useEffect } from 'react'
import axios from 'axios'
import AutocompletCorrespondant from './Autocomplet'
import Informations from './informations'
import { Form, Container, Grid, Segment, Button } from 'semantic-ui-react'

export default function Presentation() {
    const [informations, setInformations] = useState({
        prenom: "",
        nom: "",
        genre: "",
        operateur: "",
        carnetVaccination: "",
        carteIdentitePassport: "",
        carteVital: "",
        CV: "",
        permisConduire: "",
        assuranceAutomobile: "",
        photo: "",
        RIB: "",
        correspondant: ""
    })


    const handleChange = (e, { value, name }) => setInformations({ ...informations, [e.target.name || name]: value })

    const handleChangeFile = (e) => {
        setInformations({ ...informations, [e.target.name]: e.target.files })
    }

    // const sendData = () => {
    //     const data = new FormData()
    //     for (var i = 0; i < informations.length; i++) {
    //         data.append('file', informations[i])
    //     }
    //     axios({
    //         method: 'post',
    //         url: 'http://localhost:3000/upload',
    //         data: data
    //     })
    //         .then(function (reponse) {
    //             console.log(reponse, 'data bien envoyé');
    //         })
    //         .catch(function (erreur) {
    //             console.log(erreur);
    //         });
    // }

    const onChange = e => {
        setInformations(e.target.files[0]);
        // setFilename(e.target.files[0].name);
    };

    const onSubmit = async e => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('file', informations);
        try {
            const res = await axios.post('http://localhost:3000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                },
            });
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <Container>
            <Grid columns='equal'>
                <Grid.Row>
                    <Grid.Column>
                        <Form.Input fluid name='prenom' onChange={handleChange} label='Prenom' placeholder='Prenom' />
                    </Grid.Column>
                    <Grid.Column>
                        <Form.Input fluid name='nom' onChange={handleChange} label='Nom' placeholder='Nom' />
                    </Grid.Column>
                    <Grid.Column>
                        Genre
                        <Form.Radio
                            name="genre"
                            label='Homme'
                            value='homme'
                            checked={informations.genre === 'homme'}
                            onChange={handleChange}
                        />
                        <Form.Radio
                            name="genre"
                            label='Femme'
                            value='femme'
                            checked={informations.genre === 'femme'}
                            onChange={handleChange}
                        />
                    </Grid.Column>
                </Grid.Row>
            </Grid>
            <Form>
                <br />
                <Segment textAlign='center'>Les Opérateurs & N+1</Segment>
                <Form.Group inline>
                    <Form.Radio
                        name="operateur"
                        label='Doctegestio'
                        value='doctegestio'
                        checked={informations.operateur === 'doctegestio'}
                        onChange={handleChange}
                    />
                    <Form.Radio
                        name="operateur"
                        label='Doctocare'
                        value='doctocare'
                        checked={informations.operateur === 'doctocare'}
                        onChange={handleChange}
                    />
                    <Form.Radio
                        name="operateur"
                        label='Amapa'
                        value='amapa'
                        checked={informations.operateur === 'amapa'}
                        onChange={handleChange}
                    />
                    <Form.Radio
                        name="operateur"
                        label='Poppins'
                        value='poppins'
                        checked={informations.operateur === 'poppins'}
                        onChange={handleChange}
                    />
                    <AutocompletCorrespondant handleChange={handleChange} />
                </Form.Group>
                <Form.Group>
                    <Informations handleChangeFile={handleChangeFile} />
                </Form.Group>
                <Form.Group >
                </Form.Group>
            </Form>
            <Button primary onClick={sendData}>Enregistrer</Button>
        </Container >

    )
}
