import React, { Component } from 'react'
import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
} from 'semantic-ui-react'

const options = [
    { key: 'm', text: 'Monsieur', value: 'monsieur' },
    { key: 'f', text: 'Madame', value: 'madame' },

]

export default class Presentation extends Component {

    state = {}

    handleChange = (e, { value }) => this.setState({ value })

    render() {
        const { value } = this.state
        return (
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field
                        control={Input}
                        label='Prenom'
                        placeholder='Prenom'
                    />
                    <Form.Field
                        control={Input}
                        label='Nom'
                        placeholder='Nom'
                    />
                    <Form.Field
                        control={Select}
                        label='Monsieur'
                        options={options}
                        placeholder='Monsieur'
                    />
                </Form.Group>
                <Form.Group inline>
                    <label>Quantity</label>
                    <Form.Field
                        control={Radio}
                        label='One'
                        value='1'
                        checked={value === '1'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Two'
                        value='2'
                        checked={value === '2'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Three'
                        value='3'
                        checked={value === '3'}
                        onChange={this.handleChange}
                    />
                </Form.Group>
            </Form>
        )
    }
}
