import React, { useState } from 'react'
import { Dropdown } from 'semantic-ui-react'


export function AutocompletCorrespondant() {
    const data = [
        { key: 'ro', value: 'ro', flag: 'ro', text: 'Romain.Farrel' },
        { key: 'gr', value: 'gr', flag: 'gr', text: 'Gramos.immerie' },
        { key: 'ge', value: 'ge', flag: 'ge', text: 'George.Nassopulos' },
    ]

    const [correspondant, setcorrespondant] = useState({
        correspondant: "",
    })

    const handleChange = (e, { value, name }) => setcorrespondant({ ...correspondant, [e.target.name || name]: value })

    return (
        <Dropdown
            name='correspondant'
            onChange={handleChange}
            placeholder='SelectionnÃ© votre correspondant'
            fluid
            search
            selection
            options={data}
        />
    )
}




export default AutocompletCorrespondant
