import React from 'react'
import { Grid, Container, Segment, Icon } from 'semantic-ui-react'

export default function InputGenerique({ handleChangeFile, file, previewImage, setPreviewImage, setInformations }) {
    console.log(file, 'PROPS');

    const deletImage = () => {
        setPreviewImage('')
        setInformations({
            ...file,
            carteIdentitePassport: ""
        })
    }
    const titles = [{ title: 'Carte national ou passport', name: 'carteIdentitePassport' }, { title: 'CV', name: 'CV' }]
    return (
        <Container>
            <br />
            <br />
            <h3>Documents Ã  fournir par le collaborateur (10/30)</h3>
            <br />
            <Grid columns={4} divided>
                {titles.forEach(evt => {

                    <Grid.Row>
                        <Grid.Column>
                            <Segment textAlign='center'>{evt.title}</Segment>
                            {previewImage || file.carteIdentitePassport ? <div className='closeImage'>
                                <Icon name='close' color="red" className="icon-cross" onClick={deletImage} />
                                <img src={previewImage || `http://localhost:5000/static/${file.carteIdentitePassport}`} />
                            </div> : <input
                                    type="file"
                                    name={evt.name}
                                    onChange={(e) => handleChangeFile(e)}
                                />}
                        </Grid.Column>
                    </Grid.Row>

                })}
            </Grid>

        </Container>
    )
}
