import React, { useState } from 'react'
import { Grid, Container, Segment, Icon } from 'semantic-ui-react'

export default function InputImageGenerique({ informations, title, name, handleChangeFile }) {
    const [previewImage, setPreviewImage] = useState("")
    return (
        <div>
            <Grid columns={4} divided>
                <Grid.Row>
                    <Grid.Column>
                        <Segment textAlign='center'>{title}</Segment>
                        <div className="closeImage">
                            <Icon name='close' color="red" className="icon-cross" />
                            <img src={previewImage || `http://localhost:5000/static/${informations[name]}`} />
                        </div>
                        <input
                            type="file"
                            name={name}
                            onChange={(e) => {
                                handleChangeFile(e)
                                setPreviewImage(URL.createObjectURL(e.target.files[0]))
                            }}
                        />
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        </div >
    )
}
