import React from 'react'
import { Dropdown, Grid } from 'semantic-ui-react'

const data = [
    {
        key: "Oui",
        text: "Oui",
        value: "Oui",
    },
    {
        key: "Non",
        text: "Non",
        value: "Non",
    },
]

export default function ERP({ information, inforamtionRH, handleChange, disable }) {
    return (
        <Grid>
            <div className='autoCompletTitre'>Formation ERP Sécurité Incendie</div>
            <Dropdown
                placeholder='Formation ERP Sécurité Incendie'
                name="erp"
                onChange={handleChange}
                fluid
                disabled={disable}
                value={information.erp || informationRH.erp}
                selection
                options={data}
            />
        </Grid>
    )
}
