import React, { useState, useEffect } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import './App.css';
import Google from './components/googleLogin'
import InputCollaborateur from './components/inputCollaborateur';
import Rh from './components/rh'
import axios from 'axios'
import { useHistory } from "react-router-dom";



function App() {



  const [collaborateur, setCollaborateur] = useState()
  const [name, setName] = useState('')

  const collboratorResponse = (nomPrenom) => {
    setName(nomPrenom)
    axios.post("http://localhost:5000/gestionPerso", { email: localStorage.getItem("name") })
      .then(response => {
        setCollaborateur(response.data.isCollabo)
      })
  }

  useEffect(() => {
    let history = useHistory();
    collaborateur ? history.push(`/rh`) : history.push(`/collaborateur/${name}`)
  }, [collaborateur])

  return (
    <Router>
      <div>
        <Switch>
          <Route exact path='/'><Google isAdmin={collboratorResponse} />
          }</Route>
          {/* <Route exact path='/redirect/:email'><Redirection /></Route> */}
          <Route exact path='/collaborateur/:prenom/:nom'><InputCollaborateur /></Route>
          <Route exact path='/rh'><Rh /></Route>}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
