import React, { useState } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import './App.css';
import Google from './components/googleLogin'
import InputCollaborateur from './components/inputCollaborateur';
import Rh from './components/rh'
import axios from 'axios'
import { useHistory } from "react-router-dom";



function App(props) {
  console.log(props, 'PRENOM');

  const email = localStorage.getItem('name', emailResponse)
  console.log(email, 'EMAIL');

  let history = useHistory();

  const [collaborateur, setCollaborateur] = useState('')

  const handleNomPrenom = nomPrenom => setCollaborateur(nomPrenom)

  const collboratorResponse = () => {
    console.log('JE SUIS ICI');
    axios.post("http://localhost:5000/gestionPerso", { email: localStorage.getItem("name") })
      .then(response => {
        setCollaborateur(response.data.isCollabo)
      })
    // .then(collaborateur ? history.push(`/rh`) : history.push(`/collaborateur/${nomPrenom}`))
  }


  return (
    <Router>
      <div>
        <Switch>
          <Route exact path='/'><Google nomPrenom={nomPrenom => handleNomPrenom(nomPrenom)} />
          }</Route>
          {/* <Route exact path='/redirect/:email'><Redirection /></Route> */}
          <Route exact path='/collaborateur/:prenom/:nom'><InputCollaborateur /></Route>
          <Route exact path='/rh'><Rh /></Route>}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
