import React, { useState } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import './App.css';
import Google from './components/googleLogin'
import InputCollaborateur from './components/inputCollaborateur';
import Rh from './components/rh'



function App() {

  const [nomPrenom, setNomPrenom] = useState('')
  const [collabo, setCollabo] = useState({})
  console.log(collabo, 'PROPS APPSJJs');
  const handleNomPrenom = nouveauName => { if (nouveauName !== nomPrenom) setNomPrenom(nouveauName) }
  const handleAdmin = isAdmin => setCollabo(isAdmin)
  return (
    <Router>
      <div>
        <Switch>
          <Route exact path='/'><Google nomPrenom={nomPrenom => handleNomPrenom(nomPrenom)} isAdmin={isAdmin => handleAdmin(isAdmin)} />
          }</Route>
          {/* <Route exact path='/redirect/:email'><Redirection /></Route> */}
          <Route exact path='/collaborateur/:prenom/:nom'><InputCollaborateur nomPrenom={nomPrenom} collabo={collabo => handleCollabo(collabo)} /></Route>
          <Route exact path='/rh'><Rh /></Route>}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
