import React from 'react'
import { Grid, Form, Container, Segment, Icon, Button } from 'semantic-ui-react'

export default class MoresInformations extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            carnetVaccination: "",
            carteIdentitePassport: "",
            carteVital: "",
            CV: "",
            permisConduire: "",
            assuranceAutomobile: "",
            photo: "",
            RIB: "",
        };
    }

    onChangeCarnetVaccination = (e) => {
        this.setState({
            carnetVaccination: e.target.files
        });
        console.log("CarnetVaccination", e.target.files);
    };

    onChangeCV = (e) => {
        this.setState({
            CV: e.target.files,
        });
        console.log("CV", e.target.files);
    };

    onChangePassport = (e) => {
        this.setState({
            carteIdentitePassport: e.target.files,
        });
        console.log("CV", e.target.files);
    };

    onChangeCarteVital = (e) => {
        this.setState({
            carteVital: e.target.files,
        });
        console.log("CV", e.target.files);
    };


    render() {
        return (
            <Container>
                <Segment textAlign='center'>Les Informations à charger</Segment>
                <Grid columns={4} divided>
                    <Grid.Row>
                        <Grid.Column>
                            <Segment textAlign='center'>Carnet de vaccination</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangeCarnetVaccination}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>Carte national ou passport</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangePassport}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>Carte Vital</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangeCarteVital}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>CV</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangeCV}
                            />
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            <Segment textAlign='center'>Permis de conduire</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangePermis}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>Assurance automobile</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangeAutomobile}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>Photo</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangePhoto}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Segment textAlign='center'>RIB</Segment>
                            <input
                                type="file"
                                id="file"
                                onChange={this.onChangeRIB}
                            />
                        </Grid.Column>
                    </Grid.Row>
                </Grid>
            </Container >
        )
    }

}
