import React, { useState, useEffect } from 'react'
import { Dropdown, Grid } from 'semantic-ui-react'
import axios from 'axios'

export default function AutocompletFonctionDigital({ informations, informationsRH, handleChange, disable }) {

    const [data, setData] = useState([])

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios.get(
                'http://localhost:3000/fonctionData',
            );
            setData(result.data);
        };
        fetchData();
    }, []);

    return (
        <Grid>
            <div className='autoCompletTitre'>Fonction digitale principale</div>
            <Dropdown
                onChange={handleChange}
                name="fonctiondigital"
                placeholder='Fonction digitale principale'
                fluid
                search
                selection
                disabled={disable}
                value={informations.fonctiondigital || informationsRH.fonctiondigital}
                options={data}
            />
        </Grid>

    )
}
