import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Container, Header, Grid, Dropdown, Button } from 'semantic-ui-react'
import { useHistory } from "react-router-dom";

export default function Rh() {
    let history = useHistory();
    const [dataCollaborateurs, setDataCollaborateurs] = useState([])
    const [valueEmail, setValueEmail] = useState('')

    const handleChange = (e, { value, name }) => setValueEmail({ ...valueEmail, [name]: value })


    useEffect(() => {
        const fetchData = async () => {
            const result = await axios.get(
                'http://localhost:3000/emailData',
            );
            setDataCollaborateurs(result.data);
        };
        fetchData();

        axios.post("https://gsuite-api-dot-projet-test-doctegestio.uc.r.appspot.com/getGsuiteUser", { email: valueEmail.email })
            .then(res => {
                console.log(res.data, ' RH COMPOSANT');
            })

    }, []);


    const buttonRedirect = async () => {
        console.log('MARCHE OU PAS')
        const nomPrenom = valueEmail.email.split("@")[0].replace(".", "/")
        history.push(`/collaborateur/${nomPrenom}`)
    }
    console.log(valueEmail.email, 'email');

    return (
        <div>
            <Container>
                <Header as='h1' textAlign='center'>Liste des nouveaux collaborateurs</Header>
                <Grid>
                    <div className='autoCompletTitre'>Adresse e-mail du responsable </div>
                    <Dropdown
                        name='email'
                        onChange={handleChange}
                        placeholder='Collaborateur'
                        fluid
                        search
                        options={dataCollaborateurs}
                    />
                </Grid>
                <Button onClick={buttonRedirect}>DIRECTION</Button>
            </Container>
        </div>
    )
}


