import React from 'react'
import { Grid, Container, Segment, Icon } from 'semantic-ui-react'

export default function InputGenerique({ handleChangeFile, file, previewImage, setPreviewImage, setInformations }) {
    console.log(previewImage, 'PROPS');
    const deletImage = () => {
        setPreviewImage('')
        setInformations({
            ...file,
            carteIdentitePassport: ""
        })
    }


    const titles = [{ title: 'Carte national ou passport', name: 'carteIdentitePassport', previewImage: setPreviewImage(URL.createObjectURL(e.target.files[0])), img: file.carteIdentitePassport }, { title: 'carteVital', name: 'carteVital', previewImage: previewImage, img: file.carteVital }]
    return (
        <Container>
            <br />
            <br />
            <h3>Documents Ã  fournir par le collaborateur (10/30)</h3>
            <br />
            <Grid columns={4} divided>
                {titles.map(evt => {
                    return (
                        <Grid.Row>
                            <Grid.Column>
                                <Segment textAlign='center'>{evt.title}</Segment>
                                {evt.previewImage || evt.img ? <div className='closeImage'>
                                    <Icon name='close' color="red" className="icon-cross" onClick={deletImage} />
                                    <img src={evt.previewImage || `http://localhost:5000/static/${evt.img}`} />
                                </div> : <input
                                        type="file"
                                        name={evt.name}
                                        onChange={(e) => handleChangeFile(e)}
                                    />}
                            </Grid.Column>
                        </Grid.Row>
                    )
                })}
            </Grid>
        </Container>
    )
}
