import { Dropdown } from 'semantic-ui-react'

const correspondant = [
    { key: 'ro', value: 'ro', flag: 'ro', text: 'Romain.Farrel' },
    { key: 'gr', value: 'gr', flag: 'gr', text: 'Gramos.immerie' },
    { key: 'ge', value: 'ge', flag: 'ge', text: 'George.Nassopulos' },
]

const [informations, setInformations] = useState({
    correspondant: "",
})

const handleChange = (e, { value, name }) => setInformations({ ...informations, [e.target.name || name]: value })

