import React, { useState } from 'react'

export default function Uploads() {

    const [file, setFile] = useState([{
        carnetVaccination: "",
        carteIdentitePassport: "",
        carteVital: "",
        CV: "",
        permisConduire: "",
        assuranceAutomobile: "",
        photo: "",
        RIB: "",
    }])

    const onChangeCarnetVaccination = (e, value) => {
        setFile.carnetVaccination({
            ...file, [e.target.file]: file,
        });
    }

    return (
        <div>
            <input type="file" file="carnetVaccination" onChange={onChangeCarnetVaccination} style={{ display: "hidden" }} />
        </div>
    )
}
